module NotQuiteLisp.Tests

  open AdventOfCode2015
  open NUnit.Framework
  open FsUnit

  module PartOne =
    [<Test>]
    let ``findFinalFloor with '(' returns 1`` () =
      let result = NotQuiteLisp.findFinalFloor "("
      result |> should equal 1

    [<Test>]
    let ``findFinalFloor with ')' returns -1`` () =
      let result = NotQuiteLisp.findFinalFloor ")"
      result |> should equal -1

    [<Test>]
    let ``findFinalFloor with '((' returns 2`` () =
      let result = NotQuiteLisp.findFinalFloor "(("
      result |> should equal 2

    [<Test>]
    let ``findFinalFloor with '))' returns -2`` () =
      let result = NotQuiteLisp.findFinalFloor "))"
      result |> should equal -2

  module PartTwo =
    [<Test>]
    let ``findFirstBasementFloor with ')' returns 1`` () =
      let result = NotQuiteLisp.findFirstBasementFloor ")"
      result |> should equal 1

    [<Test>]
    let ``findFirstBasementFloor with '())' returns 3`` () =
      let result = NotQuiteLisp.findFirstBasementFloor "())"
      result |> should equal 3

